mapper.select:
SELECT COUNT(*) AS ISLEADER
   FROM STUDYBOARD
 WHERE STD_NO = #{grbGno}
      AND STD_ID = #{grbId}

mapper.update:
UPDATE GROUPBOARD
SET GRB_TITLE = #{grbTitle}
     , GRB_CONTENT = #{grbContent}
WHERE GRB_NO = #{grbNo}
     AND GRB_ID = #{grbId}

mapper.select:
SELECT CONCAT( GRB_UUID, '_', GRB_FILENAME ) AS FILENAME
   FROM GROUPBOARD
 WHERE GRB_NO = #{grbNo}
      AND GRB_ID = #{grbId}

mapper.update:
UPDATE GROUPBOARD
SET GRB_UUID = #{grbUuid}
     , GRB_FILENAME = #{grbFilename}
WHERE GRB_NO = #{grbNo}

mapper.select:
SELECT MAX(GRB_NO)
   FROM GROUPBOARD
 WHERE GRB_GNO = #{grbGno}
      AND GRB_ID = #{grbId}

SELECT COUNT(*)
   FROM GROUPBOARD
 WHERE GRB_GNO = #{no}

mapper.delete:
DELETE FROM GROUPMEMBER
WHERE GRM_GNO = #{grmGno}
     AND GRM_ID = #{grmId}

mapper.insert:

    SELECT COALESCE(MAX(GRM_NO+1),1) AS GRM_NO FROM GROUPMESSAGE

INSERT INTO GROUPMESSAGE( GRM_NO
                                                 , GRM_GNO
                                                 , GRM_ID
                                                 , GRM_TEXT
                                                 , GRM_DATE
                                                 )
                                    VALUES( #{grmNo}
                                                 , #{grmGno}
                                                 , #{grmId}
                                                 , #{grmText}
                                                 , NOW()
                                                 )
