mapper.update:
UPDATE GROUPBOARD
SET GRB_UUID = #{grbUuid}, GRB_FILENAME = #{grbFilename}
WHERE GRB_NO = #{grbNo}

mapper.select:
SELECT GRB_NO, GRB_GNO, GRB_ID, GRB_TITLE, GRB_CONTENT, GRB_UUID, GRB_FILENAME, GRB_AVAILABLE, GRB_DATE
FROM (
              SELECT * FROM GROUPBOARD
              WHERE GRB_GNO = #{no}
              ORDER BY GRB_DATE DESC
              LIMIT #{startRow}, #{pageSize}
) AS GROUPBOARD
ORDER BY GRB_DATE DESC
