mapper.select:
SELECT COUNT(*)
   FROM ROOM
 WHERE ( ROOM_FRUSER = #{msgFrom}
                AND ROOM_SEUSER = #{msgTo}
               )
        OR ( ROOM_SEUSER = #{msgFrom}
              AND ROOM_FRUSER = #{msgTo}
             )

SELECT ROOM_NO
   FROM ROOM


        WHERE ROOM_FRUSER = 'public'
             AND ROOM_SEUSER = 'public'


        WHERE ( ROOM_FRUSER = #{msgFrom}
                       AND ROOM_SEUSER = #{msgTo}
                      )
               OR ( ROOM_SEUSER = #{msgFrom}
                       AND ROOM_FRUSER = #{msgTo}
                      )

mapper.insert:
INSERT INTO ROOM( ROOM_NO
                                 , ROOM_FRUSER
                                 , ROOM_FRSTATE
                                 , ROOM_SEUSER
                                 , ROOM_SESTATE
                                 )


                    VALUES( #{msgRoom}
                                 , 'public'
                                 , 1
                                 , 'public'
                                 , 1
                                 )


                    VALUES( #{msgRoom}
                                 , #{msgFrom}
                                 , 1
                                 , #{msgTo}
                                 , 1
                                 )

mapper.select:
SELECT COUNT(*)
   FROM ROOM
 WHERE ( ROOM_FRUSER = #{msgFrom}
                 AND ROOM_SEUSER = #{msgTo}
               )
        OR ( ROOM_SEUSER = #{msgFrom}
                 AND ROOM_FRUSER = #{msgTo}
              )

SELECT COUNT(*)
   FROM MESSAGE
 WHERE MSG_FROM = #{msgFrom}
      AND MSG_TO = #{msgTo}
      AND MSG_RECEIVE = 1

      SELECT MSG_NO
                  , MSG_ROOM
                  , MSG_TYPE
                  , MSG_FROM
                  , MSG_TO
                  , MSG_TEXT
                  , MSG_RECEIVE
                  , MSG_DELETE
                  , MSG_DATE
                  , MBR_NICK
                  , MBR_PROFILE
        FROM (
                     SELECT * FROM MESSAGE
                      WHERE MSG_TYPE = #{msgType}
                           AND ( ( MSG_FROM = #{msgFrom}
                                        AND MSG_TO = #{msgTo}
                                      )
                                    OR
                                      ( MSG_TO = #{msgFrom}
                                        AND MSG_FROM = #{msgTo}
                                       )
                                    )
			        ) MESSAGE
		JOIN MEMBER
           ON MEMBER.MBR_ID = MESSAGE.MSG_FROM
      ORDER BY MSG_NO ASC

mapper.update:
UPDATE MESSAGE
SET MSG_RECEIVE = 0
WHERE MSG_FROM = #{msgFrom}
     AND MSG_TO = #{msgTo}
     AND MSG_RECEIVE = 1

mapper.select:
SELECT MBR_ID
   FROM MEMBER
 WHERE MBR_NICK = #{nick}

