               )
         OR ( ROOM_SEUSER = #{roomFrUser}
                 AND ROOM_FRUSER = #{roomSeUser}
                )

mapper.update:
UPDATE ROOM


          SET ROOM_FRSTATE = 0
          WHERE ROOM_NO = #{roomNo}


          SET ROOM_SESTATE = 0
          WHERE ROOM_NO = #{roomNo}
